package Assignment0;

public class HoursAndMinutes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a, hr, rm;
	    a=125;

	    hr = a / 60;
	    rm = a % 60;

	    System.out.printf("Given number = %d\n", a);
	    System.out.printf("Hours = %d\n", hr);
	    System.out.printf("Remaining Minutes = %d\n", rm);

	}

}
