package Assignment0;

public class Q2_AreaCircle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int r;                 // radius
		float a, pi = 3.14f;   // area, pi
		r = 10;                // assigning radius
		a = pi * r * r;        // area = πr²
		System.out.printf("Area of Circle = %f", a); // prints result
	}

}
