package Assignment0;

public class TotalMarksPercentage {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		    int Sub1=60, Sub2=70, Sub3=80, Sub4=40, Sub5=50;
		    double T;
		    double P;                           
		    T = Sub1 + Sub2 + Sub3 + Sub4 + Sub5; 
		    P = (T / 500) * 100;                  
		    System.out.printf("Total Marks = %f\n", T);     
		    System.out.printf("Percentage = %f\n", P);       
		}

	}

 
